import React, { Component } from "react";
import {  NavLink, withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";

import "./Sidenav.css";
import logo from "../../images/logo2.jpg";
import { faHome, faTachometerAlt, faSignOutAlt, faUsers, faDownload, faChartLine} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class SideNav extends Component {
  onLogoutClick = e => {
    this.props.logoutUser(this.props.history);
    window.location.href = "/";
  };

 
  render() {
  
    const { user } = this.props.auth;
    return (
      <div id="sidebar-collapse" class="col-sm-3 col-lg-2 sidebar">

        <ul className="top">
          
          <center><img style={{width: "100px", height:"100px", alignSelf:"center"}} src={logo} alt="lodestar logo"></img></center>
            <h6 style={{ marginBottom: "20px", fontFamily:  "Nexa Light"}}>
              <center><b>Hello Admin,</b> {user.name.split(" ")[0]}</center>
            </h6>
      
          <NavLink exact activeClassName="active-page" to="/dashboard"  style={{color: "#FF9933", fontFamily:  "Nexa Light" }}>
              <FontAwesomeIcon icon={faTachometerAlt} style={{marginRight: "30px", marginLeft: "10px"}} ></FontAwesomeIcon>Dashboard
          </NavLink>
          <NavLink exact activeClassName="active-page" to="/home" style={{color: "#FF9933", fontFamily:"Nexa Light"}}>
             <FontAwesomeIcon icon={faHome} style={{marginRight: "30px", marginLeft: "10px"}} ></FontAwesomeIcon>Home
          </NavLink>
          <NavLink exact activeClassName="active-page" to="/investor" style={{color: "#FF9933", fontFamily:  "Nexa Light"}}>
             <FontAwesomeIcon icon={faChartLine} style={{marginRight: "30px", marginLeft: "10px"}} ></FontAwesomeIcon>Investor Relation
          </NavLink>
          <NavLink exact activeClassName="active-page" to="/members" style={{color: "#FF9933", fontFamily:  "Nexa Light"}}>
              <FontAwesomeIcon icon={faUsers} style={{marginRight: "30px", marginLeft: "8px"}} ></FontAwesomeIcon>Members
          </NavLink>
          <NavLink exact activeClassName="active-page" to="/downloadables" style={{color: "#FF9933", fontFamily:  "Nexa Light"}}> 
              <FontAwesomeIcon icon={faDownload} style={{marginRight: "30px", marginLeft: "10px"}} ></FontAwesomeIcon>Downloadables
          </NavLink>
          <Link exact activeClassName="active-page" onClick={this.onLogoutClick} style={{color: "#FF9933", fontFamily:  "Nexa Light"}}> 
              <FontAwesomeIcon icon={faSignOutAlt} style={{marginRight: "30px", marginLeft: "10px"}} ></FontAwesomeIcon>Logout
          </Link>    
        </ul>
       
      </div>
    );
  }
}
const mapStateToProps = state => ({
  projects: state.projects,
  auth: state.auth
});

export default withRouter(
  connect(
    mapStateToProps,
    { logoutUser }
  )(withRouter(SideNav))
);
