import React, { Component } from "react";
import {  NavLink, withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";

import "./Sidenav.scss";
import logo from "../../images/logo2.jpg";

class SideNav extends Component {
  onLogoutClick = e => {
    this.props.logoutUser(this.props.history);
    window.location.href = "/";
  };

 
  render() {
    
    const { user } = this.props.auth;
    return (
      <nav className="side">
        <ul className="top">
          
          <center><img style={{width: "100px", height:"100px", alignSelf:"center"}} src={logo} alt="lodestar logo"></img></center>
            <h6 style={{ marginBottom: "20px"}}>
              <center><b>Hello Admin,</b> {user.name.split(" ")[0]}</center>
            </h6>
       
            
          <NavLink exact activeClassName="active-page" to="/dashboard" style={{color: "black"}}>
             <center>Dashboard</center>
          </NavLink>
          <NavLink exact activeClassName="active-page" to="/home" style={{color: "black"}}>
             <center>Home</center>
          </NavLink>
          <Link exact activeClassName="active-page" to="/investor" style={{color: "black"}}>
             <center>Investor Relation</center>
          </Link>
          <Link exact activeClassName="active-page" to="/members" style={{color: "black"}}>
             <center>Members</center>
          </Link>
          <Link exact activeClassName="active-page" to="/downloadables" style={{color: "black"}}> 
             <center>Downloadables</center>
          </Link>
          <Link exact activeClassName="active-page" onClick={this.onLogoutClick} style={{color: "black"}}> 
             <center>Logout</center>
          </Link>    
        </ul>
       
      </nav>
    );
  }
}
const mapStateToProps = state => ({
  projects: state.projects,
  auth: state.auth
});

export default withRouter(
  connect(
    mapStateToProps,
    { logoutUser }
  )(withRouter(SideNav))
);
